CREATE OR REPLACE FUNCTION dev_settler.public.trg_prv_object_hierarchy_insert()
              RETURNS TRIGGER AS $trg_prv_object_hierarchy_insert$
BEGIN
EXECUTE dev_settler.public.proc_jtb_object_hierarchy_insert(NEW.prv_object_from, NEW.prv_object_to);
RETURN NULL;
END;
$trg_prv_object_hierarchy_insert$ LANGUAGE plpgsql;

